    headID.appendChild(newScript);
    scriptsSrc.push('https://pnp-partner-pack.azurewebsites.net:443/Templates/Overrides/PnP-Partner-Pack-Overrides.js?rev=457cefaa69cc4d6d9d37cecc6cb04dc3');
